package com.syntax.class07;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		  int g = 70;
		  do {
			  System.out.print(g+" ");
			  g-=2;
			  		  }			
			 while (g >= 30); 
		  
System.out.println("---------------------------");
		  

int h = 70;
		  do {
			  System.out.print(h+" ");
			  h--;
			  		  }			
			 while (h >= 30); 
				
			}
		
	}
	


